	s = " " + s;
	f[0][0][0] = 1;

	for(int i = 0; i < m; i++)
		for(int j = 0; j <= min(i, n); j++)
			for(int k = 0; k <= i; k++)
				if(f[i][j][k])
				{
					if(k < m && (j == n || s[j + 1] != '('))
						add(f[i + 1][j][k + 1], f[i][j][k]);
					if(k && (j == n || s[j + 1] != ')'))
						add(f[i + 1][j][k - 1], f[i][j][k]);
					if(j < n)
					{
						if(k < m && s[j + 1] == '(')
							add(f[i + 1][j + 1][k + 1], f[i][j][k]);
						if(k && s[j + 1] == ')')
							add(f[i + 1][j + 1][k - 1], f[i][j][k]);
					}
				}

	cout << f[m][n][0] << "\n";
}
