const int mod = 1e9 + 7;
const ld eps = 1e-8;

int f[N][N][N];

void add(int &x, int y)
{
	if((x += y) >= mod)
		x -= mod;
}
void solve()
{
	memset(f, 0, sizeof f);
	int n, m;
	cin >> n >> m;
	string s;
	cin >> s;
	s = " " + s;
	f[0][0][0] = 1;

	for(int i = 0; i < m; i++)
		for(int j = 0; j <= min(i, n); j++)
			for(int k = 0; k <= i; k++)
				if(f[i][j][k])
				{
					if(k < m && (j == n || s[j + 1] != '('))
						add(f[i + 1][j][k + 1], f[i][j][k]);
					if(k && (j == n || s[j + 1] != ')'))
						add(f[i + 1][j][k - 1], f[i][j][k]);
					if(j < n)
					{
						if(k < m && s[j + 1] == '(')
							add(f[i + 1][j + 1][k + 1], f[i][j][k]);
						if(k && s[j + 1] == ')')
							add(f[i + 1][j + 1][k - 1], f[i][j][k]);
					}
				}

	cout << f[m][n][0] << "\n";
}
int main()
{
	ios::sync_with_stdio(false);
	cin.tie(0);

	int t;
	t = 1;
	cin >> t;
	while(t--)
		solve();
	return 0;
}
