		{
			sum += v;//累加
			if(dep[u] + d < n - 1)
				b[dep[u] + d + 1] -= v;//mark
		}
		ans[u] = sum;
		for(auto v : g[u])
		{
			if(v == fa) continue;
			dep[v] = dep[u] + 1;
			dfs(v, u, sum);
		}
        // 删除标记
		for(auto [d, v] : q[u])
		{
			if(dep[u] + d < n - 1)
				b[dep[u] + d + 1] += v;
		}
	};
	dfs(1, -1, 0);
	for(int i = 1; i <= n; i++)
		cout << ans[i] << " \n"[i == n];
	return 0;
}
```

